import io from 'socket.io-client'
const socket = io.connect("http://localhost:3456")
const App = () => {
  const sendMessage = ()=>{
   socket.emit("send_message",{message:'Hello'})

  }
  return (
    <div>
    <input placeholder="Message... "/>
    <button onClick={sendMessage}>
      send a message
    </button>
    </div>
  )
}
export default App
